mapper.select:
SELECT

FROM purchase_information


    and category_first_id = #{categoryFirstId}


    and category_second_id = #{categorySecondId}


    and province_code = #{provinceCode}


    and city_code = #{cityCode}


    and area_code = #{areaCode}


    and (
    title like concat('%', #{keyword}, '%')
    or new_old_level like concat('%', #{keyword}, '%')
    or content like concat('%', #{keyword}, '%')
    )


    and contact_mobile like concat('%', #{contactMobile}, '%')


    and shelf_status = #{shelfStatus}


    and creator = #{creator}


    and brand_id = #{brandId}


order by id desc
